import {useEffect, useState} from 'react';
import { Container, Form } from 'react-bootstrap';

import './App.css';

const App = (props) => {

  const [defaultSize, setDefaultSize] = useState(props.defaultSize);
  const [sizePx, setSizePx] = useState();
  const [sizeRem, setSizeRem] = useState(1);
  const [direct, setDirect] = useState();

  useEffect(() => {
    if ( direct == 'rem' ) {
    setSizePx(defaultSize);
    }
  }, [defaultSize, direct])

  useEffect(() => {
    setSizePx(sizeRem * defaultSize);
  }, [sizeRem, defaultSize])

  // useEffect(() => {
  //   setSizePx(sizeRem * defaultSize);
  // }, [defaultSize, sizeRem])

  const changeRem = (event) => {
    setSizeRem(event.target.value);
    setDirect('rem');
  }

  return (
    <Container>
    <div className="App">
      <header className="App-header">
        <p className="text-center" style={{fontSize: sizePx}}>
          rem-to-px / px-to-rem Converter
        </p>
      </header>
      <main>
        <Form>

          <Form.Group className="mb-3">
            <Form.Label>REM</Form.Label>
            <Form.Control 
              type="text"
              autoComplete="off"
              name="inputRem"
              defaultValue={sizeRem}
              onChange={changeRem} />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>PX</Form.Label>
            <Form.Control 
              type="text"
              autoComplete="off"
              name="inputPx"
              defaultValue={sizePx}
              onChange={event => setSizePx(event.target.value)} />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>1rem =</Form.Label>
            <Form.Control 
              type="text"
              autoComplete="off"
              defaultValue={defaultSize}
              onChange={event => setDefaultSize(event.target.value)} />
            <p>{defaultSize}</p>
          </Form.Group>

        </Form>        
      </main>
    </div>
    </Container>
  );
}

export default App;
