import {useEffect, useState} from 'react';
import { Container, Row, Col, Form } from 'react-bootstrap';

import './App.scss';

const App = (props) => {

  const [defaultSize, setDefaultSize] = useState(props.defaultSize);
  const [sizePx, setSizePx] = useState();
  const [sizeRem, setSizeRem] = useState(1);
  const [sizePreview, setSizePreview] = useState(props.defaultSize);
  const [directCalc, setDirectCalc] = useState('rem');

  useEffect(() => {
    setSizePx(defaultSize);
  }, [])

  useEffect(() => {
    console.log(directCalc);    
     if ( directCalc === 'rem' ) {
       setSizePx(sizeRem * defaultSize);
       setSizePreview(sizePx + 'px');
     }
     if ( directCalc === 'px' ) {
       setSizeRem(sizePx / defaultSize);
       setSizePreview(sizePx + 'px');
     }

  }, [sizeRem, sizePx, directCalc, defaultSize])

  const changeRem = (event) => {
    setSizeRem(event.target.value);
    setDirectCalc('rem');
  }

  const changePx = (event) => {
    setSizePx(event.target.value);
    setDirectCalc('px');
  }  

  return (
    <Container>
    <Row>
      <Col sm={6}>
        <h2>Preview:</h2>
        <p className="preview" style={{fontSize: sizePreview}}>
          Lorem ipsum...
        </p>
      </Col>
      <Col sm={6}>
        <Form>

          <Form.Group className="mb-3">
            <Form.Label>REM</Form.Label>
            <Form.Control 
              type="text"
              autoComplete="off"
              name="inputRem"
              value={sizeRem}
              onChange={changeRem} />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>PX</Form.Label>
            <Form.Control 
              type="text"
              autoComplete="off"
              name="inputPx"
              value={sizePx}
              onChange={changePx} />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>1rem =</Form.Label>
            <Form.Control 
              type="text"
              autoComplete="off"
              value={defaultSize}
              onChange={event => setDefaultSize(event.target.value)} />
          </Form.Group>

        </Form>        
      </Col>
    </Row>
    </Container>
  );
}

export default App;
