import {useEffect, useState} from 'react';
import { Container, Form } from 'react-bootstrap';

import './App.css';

const App = (props) => {

  const [defaultSize, setDefaultSize] = useState(props.defaultSize);
  const [sizePx, setSizePx] = useState();
  const [sizeRem, setSizeRem] = useState(1);
  const [directCalc, setDirectCalc] = useState(null);

  useEffect(() => {
    setSizePx(defaultSize);
  }, [])

  useEffect(() => {
    console.log(directCalc);    
    // if ( directCalc == 'rem' ) {
    //   setSizePx(sizeRem * defaultSize);
    // }; 
    // if ( directCalc == 'px' ) {
      setSizeRem( (sizePx/defaultSize) );
    // }

  }, [sizeRem, sizePx, defaultSize, directCalc])

  const changeRem = (event) => {
    setSizeRem(event.target.value);
    setDirectCalc('rem');
  }

  const changePx = (event) => {
    setSizePx(event.target.value);
    setDirectCalc('px');
  }  

  return (
    <Container>
    <div className="App">
      <header className="App-header">
        <p className="text-center" style={{fontSize: sizePx}}>
          rem-to-px / px-to-rem Converter
        </p>
      </header>
      <main>
        <Form>

          <Form.Group className="mb-3">
            <Form.Label>REM</Form.Label>
            <Form.Control 
              type="text"
              autoComplete="off"
              name="inputRem"
              defaultValue={sizeRem}
              onChange={changeRem} />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>PX</Form.Label>
            <Form.Control 
              type="text"
              autoComplete="off"
              name="inputPx"
              defaultValue={sizePx}
              onChange={changePx} />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>1rem =</Form.Label>
            <Form.Control 
              type="text"
              autoComplete="off"
              defaultValue={defaultSize}
              onChange={event => setDefaultSize(event.target.value)} />
            <p>{defaultSize}</p>
          </Form.Group>

        </Form>        
      </main>
    </div>
    </Container>
  );
}

export default App;
