import {useState} from 'react';
import { Container, Button, Form } from 'react-bootstrap';
import './App.css';

const App = (props) => {

  const [defaultSize, setDefaultSize] = useState(props.defaultSize);
  const [sizePx, setSizePx] = useState(16);
  const [sizeRem, setSizeRem] = useState(1);

  function SetDefaultSize() {
    console.log('change defaultSize');
  }


  return (
    <Container>
    <div className="App">
      <header className="App-header">
        <p className="text-center" style={{fontSize: defaultSize}}>
          rem-to-px / px-to-rem Converter
        </p>
      </header>
      <main>
        <Form>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="text" defaultValue={sizeRem} />
            <Form.Text className="text-muted">
              We'll never share your email with anyone else.
            </Form.Text>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="text" defaultValue={sizePx} />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicDefault">
            <Form.Label>1rem =</Form.Label>
            <Form.Control type="text" defaultValue={defaultSize} onClick={SetDefaultSize} />
          </Form.Group>

        </Form>        
      </main>
    </div>
    </Container>
  );
}

export default App;
